"use strict";const header=document.querySelector(".header"),language=document.getElementById("language");language.addEventListener("click",e=>{e.preventDefault()}),window.addEventListener("scroll",()=>{0==pageYOffset?header.classList.remove("scrolled"):header.classList.add("scrolled")});const menuLinks=document.querySelectorAll(".nav__link[data-goto]");if(menuLinks.length>0){function onMenuLinkClick(e){const t=e.target;if(t.dataset.goto&&document.querySelector(t.dataset.goto)){const n=document.querySelector(t.dataset.goto).getBoundingClientRect().top+pageYOffset-document.querySelector("header").offsetHeight;window.scrollTo({top:n,behavior:"smooth"}),e.preventDefault()}}menuLinks.forEach(e=>{e.addEventListener("click",onMenuLinkClick)})}